import "./mealItem.css"
import MealItemForm from "./MealItemForm";

function MealItem(props)
{
    return(
        <li className="box">
            <div>
            <h3>
                {props.item.name}
            </h3>
            <p className="description">
                {props.item.description}
            </p>
            
            <h2 className="price">
                ${props.item.price.toString()}
            </h2>
            </div>
            <MealItemForm id={props.item.id}/>
        </li>
    )
}

export default MealItem;
